import React from 'react'
import { useState } from 'react'


const Hint = ({proper}) =>{
    const hint = proper ? '' : 'Too many matches, specify another filter';
    return (
        <div><p>{hint}</p></div>
    );
};


const Button = () =>{
    return(
        <button >show</button>
    )
}


const Many = ({result, proper, num, buttonOnChange}) =>{
    const res = proper && num !==1 ? result : [];
    return (
        <div>
        {res.map(

            (r, i) => <p key={i}>{r.name.common} <Button /></p>

            )}
        </div>
    );

};


const Single = ({result, num}) =>{
    if (num === 1){
        const name = result[0].name.common;
        const lang = result[0].languages;
        const flag = result[0].flags.png;
        const capital = result[0].capital;
        const area = result[0].area;
        return (

            <div key={1}>
                <h1>{name}</h1>
                <p>capital {capital.map(c=>c)}</p>
                <p>area {area}</p>
                <h2>languages:</h2>
                <ul>
                {

                Object.keys(lang).map(

                    (k, i) => <li key={i}>
                                    {lang[k]}
                              </li>
                )

                }
                </ul>
                <div key={2}>
                    <img src={flag}/>
                </div>

            </div>

        );
    }else{

        return(
            <div></div>
        )
    }

};


const Results = (props) => {
    const {results, buttonOnChange} = props;
    const [result, setFilter] = useState(results);
    const resultNum = result.length;
    const isProper = resultNum < 10 && resultNum > 0;

    console.log('Result组件', result);
    console.log('结果数量', isProper);

    const onChangeHandler = (index) =>{
        setFilter([].concat(results[index]));

      };
    return (
        <div>
            <Hint proper={isProper}/>
            <Many result={result} proper={isProper} num={resultNum} onChangeHandler={onChangeHandler}/>
            <Single result={result}  num={resultNum}/>
        </div>
    );
};

export default Results;
